import React from 'react';
import item1 from "../../assets/img/item_1.png";
import shape1 from "../../assets/img/shape-down-1.png";
import heart from "../../assets/img/heart.png";
import cart from "../../assets/img/cart.png";
import { useDispatch } from 'react-redux';
import { addCart, decreaseCart, increaseCart } from '../../reducks/carts/operations'



const Item = ({setShowRev,setCheckRev, item}) => { 
        const dispatch=useDispatch();
        const clickAddCart=()=>{
            dispatch(addCart(item));
        };
        const clickPlusCart =()=>{
            dispatch(increaseCart(item));
        };
        const clickMinusCart=()=>{
            dispatch(decreaseCart(item));
        };


  return (
    <>

            <div className="item">
                <div className="item-image">
                    <img className="dil" src={item.image} alt="" />
                    <div className="white-shade">
                        <img src={shape1} alt="" />
                    </div>
                </div>
                <div className="like">
                    <img src={heart} alt="" />
                    <p>(15)</p>
                </div>
                <div className="name">
                    <h1>{item.name}</h1>
                </div>
                <div className="review" style={{display:'flex'}}>
                    <div onClick={()=>setShowRev(true)}>Write Review</div>
                    <div onClick={()=> setCheckRev(true)}>Check Review</div>
                </div>
                <div className="cart_price">
                    <div className="cart">
                        <button>+</button>
                        <button class='cartbutton' onClick={clickAddCart}>

                        <img src={cart} />Add to Cart</button>
                        <button>-</button>
                    </div>
                    <div className="price">
                        <p>$ {item.PRICE}</p>
                    </div>
                </div>
            </div>


    </>
  )
}

export default Item
